@use 'sass:color';
@import url("./header.scss");
// Test
$blue: rgba(45, 156, 219, 0.7);
$green: rgba(39, 174, 96, 0.7);
$red: rgba(235, 87, 87, 0.7);
$grey: #e0e0e0;

:root {
  --clr-blue: rgba(45, 156, 219, 0.7);
  --clr-green: rgba(39, 174, 96, 0.7);
  --clr-red: rgba(235, 87, 87, 0.7);
  --clr-gray1: #333333;
  --clr-gray2: #4f4f4f;
  --clr-gray3: #828282;
  --clr-gray4: #bdbdbd;
  --clr-gray6: #f2f2f2;
}
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  list-style: none;
  box-sizing: border-box;
}

html {
  font-family: "Poppins", sans-serif;
  font-size: 10px;
}

h1,
h2,
h3,
h4,
h5,
h6,
a,
p,
input {
  font-family: "Poppins", sans-serif;
}

p {
  font-size: 1.2rem;
  line-height: 1.8rem;
  color: var(--clr-gray2);
}

.container {
  max-width: 1110px;
  padding: 0 40px;
  margin: 0 auto;
}

.container-lg {
  padding: 0 10px !important;
}

.container-xl {
  padding: 0 10px !important;
}

h2 {
  font-size: 2.4rem;
  line-height: 3.6rem;
  font-weight: 500;
  color: var(--clr-gray1);
}

img {
  width: 100%;
  max-width: 100%;
  height: auto;
  border-radius: 24px;
}

.btn {
  padding: 10px 20px;
  border-radius: 12px;
  border: 0;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  transition: background-color 0.3s ease-in;
  cursor: pointer;
}

.btn-default {
  background-color: $grey;
  color: #828282;

  &:hover {
    background-color: color.scale($grey, $lightness: -10%);
  }
}
.btn-blue,
.btn-green,
.btn-red {
  color: white;
}
.btn-blue {
  background-color: $blue;

  &:hover {
    background-color: color.scale($blue, $lightness: -30%);
  }
}
.btn-green {
  background-color: $green;

  &:hover {
    background-color: color.scale($green, $lightness: -30%);
  }
}
.btn-red {
  background-color: $red;
  &:hover {
    background-color: color.scale($red, $lightness: -30%);
  }
}

section {
  margin-bottom: 100px;
}

.form {
  margin-top: 31px;
}
.form label {
  font-size: 1rem;
  line-height: 1.5rem;
  color: var(--clr-gray3);
}
.btn-input {
  background-color: #f2f2f2;
  height: 47px;
  border-radius: 12px;
  padding: 4px 4px 4px 19px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 4px;
}
input {
  outline: none;
  background-color: transparent;
  border: none;
  max-width: 60%;
}
input::placeholder {
  font-size: 1.4rem;
  line-height: 2.1rem;
  color: var(--clr-gray4);
}

// Home
.home {
  margin-top: 48px;

  .question {
    font-size: 1.2rem;
    line-height: 1.8rem;
    font-weight: 500;
    color: var(--clr-blue);
  }

  .home__image {
    margin-bottom: 22px;
    margin-top: 15px;
    border-radius: 18px;
  }

  p {
    margin-top: 33px;
  }

  .form {
    margin-top: 31px;
  }
}

// Services
.feature {
  margin-top: 94px;
  padding: 46px 35px;

  h3 {
    margin-top: 3.5rem;
    font-weight: bold;
    font-size: 2.4rem;
    line-height: 3.6rem;
    color: var(--clr-gray1);
  }
  p {
    margin-top: 2.4rem;
    font-size: 1.6rem;
    line-height: 2.4rem;
    color: var(--clr-gray2);
  }
  .btn {
    margin-top: 30px;
  }

  &.rounded {
    background: #ffffff;
    box-shadow: 0px 10px 30px rgba(51, 51, 51, 0.1);
    border-radius: 24px;
  }
}

.feat-icon {
  height: 67px;
  width: 67px;
  display: flex;
  justify-content: center;
  align-items: center;
}

// Our Works
.works {
  display: initial;
}
.work {
  margin-top: 4.8rem;

  &__image {
    margin-bottom: 1.2rem;
  }
  &__type {
    font-weight: 300;
    font-size: 1.2rem;
    line-height: 1.8rem;
    color: var(--clr-gray3);
  }

  &__title {
    font-size: 1.8rem;
    line-height: 2.7rem;
    color: var(--clr-gray1);
  }
}

.link-special {
  text-decoration: none;
  color: var(--clr-blue);
  font-size: 18px;
  line-height: 27px;
  display: flex;
  align-items: center;
  margin-top: 3.5rem;
}
.link-special span {
  margin-right: 12px;
}

// Clients
.clients {
  &__subtitle {
    font-weight: 500;
    font-size: 1.8rem;
    line-height: 2.7rem;
    color: var(--clr-red);
  }

  p {
    font-size: 1.6rem;
    line-height: 2.4rem;
    margin-top: 1.4rem;
    padding-right: 9.2rem;
  }

  &__gallery {
    margin-top: 3.6rem;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: repeat(4, 70px);
    column-gap: 8px;
    row-gap: 8px;

    & img {
      height: 100%;
      object-fit: cover;
    }

    & img:nth-child(1) {
      grid-column: 2/3;
      grid-row: 1/3;
    }
    & img:nth-child(2) {
      grid-column: 2/3;
      grid-row: 3/5;
    }
    & img:nth-child(3) {
      grid-column: 1/2;
      grid-row: 2/4;
    }
  }

  &__quote {
    font-size: 2.4rem;
    line-height: 3.6rem;
    margin-top: 100px;
  }

  &__person-box {
    display: flex;
    margin-top: 2.7rem;

    & img {
      width: 8.2rem;
      height: 8.2rem;
    }

    .details {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      padding: 4px 0;
      margin-left: 3rem;

      &__name {
        font-size: 2.4rem;
        line-height: 3.6rem;
        color: var(--clr-gray1);
      }

      &__nickname {
        font-weight: 500;
        font-size: 1.8rem;
        line-height: 2.7rem;
        color: var(--clr-gray3);
      }
    }
  }
}

// Contact
.contact {
  background-color: black;
  padding: 36px 16px;

  // &__items {
  //   display: flex;
  // }

  .menu-footer {
    li {
      a {
        font-size: 1.8rem;
        line-height: 200%;
        color: white;
        text-decoration: none;
      }
    }
  }

  .logo-footer {
    margin-top: 6.4rem;
    font-weight: 800;
    font-size: 3.6rem;
    line-height: 5.3rem;
    color: var(--clr-gray6);
  }

  .social {
    display: flex;
    margin-top: 0.8rem;

    & a:not(:last-child) {
      margin-right: 1.2rem;
    }
  }

  .form-footer {
    margin-top: 6.4rem;
  }

  .copyright {
    margin-top: 100px;
    font-weight: 600;
    font-size: 1.4rem;
    line-height: 1.7rem;
    color: #a9a9a9;
    text-align: center;
  }
}

// Media Queries
@media screen and (min-width: 767px) {
  .container-lg {
    padding: 0 40px !important;
  }

  h2 {
    font-size: 4.8rem;
    line-height: 7.2rem;
  }

  .home {
    p {
      font-size: 1.8rem;
      line-height: 2.7rem;
    }
  }

  .form {
    label {
      font-size: 1.4rem;
      line-height: 2.1rem;
    }
  }
  input::placeholder {
    font-size: 1.8rem;
    line-height: 2.7rem;
  }

  .work {
    &__type {
      font-size: 1.4rem;
      line-height: 2.1rem;
    }
  }

  .features-list {
    display: flex;
  }

  .clients__container {
    display: flex;
    align-items: center;
  }

  .contact {
    &__items {
      display: flex;
      justify-content: space-between;
    }
    .logo-footer {
      margin-top: 0;
    }
    .form-footer {
      margin-top: 0;
    }
  }
}

@media screen and(min-width: 860px) {
  .container {
    padding: 0 150px;
  }

  .container-sm {
    max-width: 370px;
  }

  .container-lg {
    padding: 0 100px !important;
  }

  .home {
    margin-top: 100px;
  }
  .home__image {
    margin: 42px 0;
  }

  .works {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: repeat(5, auto);
    column-gap: 30px;
    row-gap: 30px;

    .work:nth-child(1) {
      grid-column: 1/2;
      grid-row: 2/4;
    }
    .work:nth-child(2) {
      grid-column: 2/2;
      grid-row: 1/3;
    }
    .work:nth-child(3) {
      grid-column: 1/2;
      grid-row: 4/6;
    }
    .work:nth-child(4) {
      grid-column: 2/2;
      grid-row: 3/5;
    }
  }

  .link-special {
    justify-content: flex-end;
    margin-top: -26px;
  }

  .clients__gallery {
    grid-template-rows: repeat(4, 90px);
  }
}

@media screen and (min-width: 1200px) {
  .container {
    padding: 0 250px;
  }
  .clients__gallery {
    grid-template-rows: repeat(4, 130px);
  }
}
