  --clr-red: rgba(235, 87, 87, 0.7);
  --clr-gray1: #333333;
  --clr-gray2: #4f4f4f;
  --clr-gray3: #828282;
  --clr-gray4: #bdbdbd;
  --clr-gray6: #f2f2f2;
