  font-size: 1.2rem;
  line-height: 1.8rem;
  color: var(--clr-gray2);
}

.container {
  max-width: 1110px;
  padding: 0 40px;
  margin: 0 auto;
}

.container-lg {
  padding: 0 10px !important;
}

.container-xl {
  padding: 0 10px !important;
}
