//   max-width: 320px;
//   margin: initial;
// }

h2 {
  font-size: 2.4rem;
  line-height: 3.6rem;
  font-weight: 500;
  color: var(--clr-gray1);
}

img {
  width: 100%;
  max-width: 100%;
  height: auto;
  border-radius: 24px;
}

.btn {
  padding: 10px 20px;
  border-radius: 12px;
  border: 0;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
}

.btn-default {
  background-color: #e0e0e0;
  color: #828282;
}
.btn-blue,
.btn-green,
.btn-red {
  color: white;
}
.btn-blue {
  background: var(--clr-blue);
}
.btn-green {
  background: var(--clr-green);
}
.btn-red {
  background: var(--clr-red);
}

section {
  margin-bottom: 100px;
}

.form {
  margin-top: 31px;
}
.form label {
  font-size: 10px;
  line-height: 15px;
  color: var(--clr-gray3);
}
.btn-input {
  background-color: #f2f2f2;
  height: 47px;
  border-radius: 12px;
  padding: 4px 4px 4px 19px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 4px;
}
