// Home
.home {
  margin-top: 48px;

  .question {
    font-size: 1.2rem;
    line-height: 1.8rem;
    font-weight: 500;
    color: var(--clr-blue);
  }

  .home__image {
    margin-bottom: 22px;
    margin-top: 15px;
    border-radius: 18px;
  }

  p {
    margin-top: 33px;
  }

  .form {
    margin-top: 31px;
  }
}

// Services
.feature {
  margin-top: 94px;
  padding: 46px 35px;

  h3 {
    margin-top: 3.5rem;
    font-weight: bold;
    font-size: 2.4rem;
    line-height: 3.6rem;
    color: var(--clr-gray1);
