  text-decoration: none;
  color: var(--clr-blue);
  font-size: 18px;
  line-height: 27px;
  display: flex;
  align-items: center;
  margin-top: 3.5rem;
}
.link-special span {
  margin-right: 12px;
}

// Clients
.clients {
  &__subtitle {
    font-weight: 500;
    font-size: 1.8rem;
    line-height: 2.7rem;
    color: var(--clr-red);
  }

  p {
    font-size: 1.6rem;
    line-height: 2.4rem;
    margin-top: 1.4rem;
    padding-right: 9.2rem;
  }

