  &__quote {
    font-size: 2.4rem;
    line-height: 3.6rem;
    margin-top: 100px;
  }

  &__person-box {
    display: flex;
    margin-top: 2.7rem;

    & img {
      width: 8.2rem;
      height: 8.2rem;
    }

    .details {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      padding: 4px 0;
      margin-left: 3rem;

      &__name {
        font-size: 2.4rem;
        line-height: 3.6rem;
        color: var(--clr-gray1);
      }

      &__nickname {
        font-weight: 500;
        font-size: 1.8rem;
        line-height: 2.7rem;
        color: var(--clr-gray3);
      }
    }
  }
