// Contact
.contact {
  background-color: black;
  padding: 36px 16px;

  // &__items {
  //   display: flex;
  // }

  .menu-footer {
    li {
      a {
        font-size: 1.8rem;
        line-height: 200%;
        color: white;
        text-decoration: none;
      }
    }
  }

  .logo-footer {
    margin-top: 6.4rem;
    font-weight: 800;
    font-size: 3.6rem;
    line-height: 5.3rem;
    color: var(--clr-gray6);
  }

  .social {
    display: flex;
    margin-top: 0.8rem;

    & a:not(:last-child) {
      margin-right: 1.2rem;
    }
  }

  .form-footer {
    margin-top: 6.4rem;
  }

  .copyright {
    margin-top: 100px;
    font-weight: 600;
    font-size: 1.4rem;
    line-height: 1.7rem;
    color: #a9a9a9;
    text-align: center;
  }
}

// Media Queries
@media screen and (min-width: 767px) {
  .container-lg {
    padding: 0 40px !important;
  }
  .container-sm {
    max-width: 50%;
  }
  .features-list {
    display: flex;
  }

  .clients__container {
    display: flex;
    align-items: center;
  }

  .contact {
    &__items {
      display: flex;
      justify-content: space-between;
    }
    .logo-footer {
      margin-top: 0;
    }
    .form-footer {
      margin-top: 0;
    }
  }
}

@media screen and(min-width: 860px) {
  .container {
    padding: 0 150px;
  }

  .container-lg {
    padding: 0 100px !important;
  }

  .home {
    margin-top: 100px;
  }
  .home__image {
    margin: 42px 0;
  }

  .works {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: repeat(5, auto);
    column-gap: 30px;
    row-gap: 30px;

    .work:nth-child(1) {
      grid-column: 1/2;
      grid-row: 2/4;
    }
    .work:nth-child(2) {
      grid-column: 2/2;
      grid-row: 1/3;
    }
    .work:nth-child(3) {
      grid-column: 1/2;
      grid-row: 4/6;
    }
    .work:nth-child(4) {
      grid-column: 2/2;
      grid-row: 3/5;
    }
  }

  .link-special {
    justify-content: flex-end;
    margin-top: -26px;
  }

  .clients__gallery {
    grid-template-rows: repeat(4, 90px);
  }
}

@media screen and (min-width: 1200px) {
  .container {
    padding: 0 250px;
  }
  .clients__gallery {
    grid-template-rows: repeat(4, 130px);
  }
}
